    public function generarColores()
    {

        $opciones = Option::all();
        $colores =[
            'primary',
//            'secondary',
            'success',
            'info',
            'warning',
            'danger',
        ];

        foreach ($opciones as $key => $opcion) {
            $colorAleatorio = $colores[array_rand($colores)];
            $opcion->color = $colorAleatorio;
            $opcion->save();
        }

    }

